import type { PostEvent } from '../bridge/methods/postEvent.ts';
import type { BackButton } from '../components/back-button/BackButton.ts';
import type { ClosingBehavior } from '../components/closing-behavior/ClosingBehavior.ts';
import type { CloudStorage } from '../components/cloud-storage/CloudStorage.ts';
import type { HapticFeedback } from '../components/haptic-feedback/HapticFeedback.ts';
import type { InitData } from '../components/init-data/InitData.ts';
import type { Invoice } from '../components/invoice/Invoice.ts';
import type { MainButton } from '../components/main-button/MainButton.ts';
import type { MiniApp } from '../components/mini-app/MiniApp.ts';
import type { Popup } from '../components/popup/Popup.ts';
import type { QRScanner } from '../components/qr-scanner/QRScanner.ts';
import type { SettingsButton } from '../components/settings-button/SettingsButton.ts';
import type { ThemeParams } from '../components/theme-params/ThemeParams.ts';
import type { Utils } from '../components/utils/Utils.ts';
import type { Viewport } from '../components/viewport/Viewport.ts';
import type { CreateRequestIdFunc } from '../types/request-id.ts';

export interface InitResult {
  backButton: BackButton;
  closingBehavior: ClosingBehavior;
  cloudStorage: CloudStorage;
  createRequestId: CreateRequestIdFunc;
  hapticFeedback: HapticFeedback;
  initData?: InitData;
  initDataRaw?: string;
  invoice: Invoice;
  mainButton: MainButton;
  miniApp: MiniApp;
  popup: Popup;
  postEvent: PostEvent;
  qrScanner: QRScanner;
  settingsButton: SettingsButton;
  themeParams: ThemeParams;
  utils: Utils;
  viewport: Viewport;
}

export interface InitCSSVarsSpecificOption {
  /**
   * Enables theme parameters CSS variables.
   * @see bindThemeCSSVars
   * @default false
   */
  themeParams?: boolean;

  /**
   * Enables viewport CSS variables.
   * @see bindViewportCSSVars
   * @default false
   */
  viewport?: boolean;

  /**
   * Enables mini app CSS variables.
   * @see bindMiniAppCSSVars
   * @default false
   */
  miniApp?: boolean;
}

export type InitCSSVarsOption = boolean | InitCSSVarsSpecificOption;

export interface InitOptions {
  /**
   * @deprecated This option name was considered inappropriate. Use `complete` instead.
   */
  async?: boolean;

  /**
   * True if SDK should accept styles sent from the Telegram web application. This option is only
   * used in web versions of Telegram.
   * @default false
   */
  acceptCustomStyles?: boolean;

  /**
   * Should SDK create global CSS variables related to current Telegram application colors.
   *
   * Possible values:
   * - `false` - no CSS variables will be created.
   * - `true` - all CSS variables will be created.
   * - object - applies specific CSS variables.
   *
   * @default false
   */
  cssVars?: InitCSSVarsOption;

  /**
   * True if initialization must be performed completely. This includes retrieving some components
   * state from the Telegram application, and as a result, this makes initialization asynchronous.
   * @default false
   */
  complete?: boolean;
}
